import logo from "./logo.svg";
import React, { Component } from "react";
import "./App.css";
/*
const list = [
  {
    title: "React",
    url: "https://facebook.github.io/react/",
    author: "Jordan Walke",
    num_comments: 3,
    points: 4,
    objectID: 0,
  },
  {
    title: "Redux",
    url: "https://github.com/reactjs/redux",
    author: "Dan Abramov, Andrew Clark",
    num_comments: 2,
    points: 5,
    objectID: 1,
  },
];
*/

const list = [
  {
    title: 'Current State here',
    url: 'https://facebook.github.io/react/',
    num_comments: 5,
    points: 4,
    objectID: 0,
  },
  {
    title: 'joe',
    url: 'http',
    num_comments: 5,
    points: 4,
    objectID: 23,
  },
];

const exampleObject = [ {
  firstname: 'something',
  lastname: 'here',
  },
];


const exampleService = {
  getNewService(user) {
    return user.firstname + ' ' + user.lastname;
  },
};


const key = 'name';
const user = {
  [key]: 'Robin',
};

class App extends Component {
    
  
  
  constructor(props) {
    super(props);

    this.state = {
      list,
    };

    this.onDismiss = this.onDismiss.bind(this);


  }
//Leared on page 52
  onDismiss(id) {
    const updatedList = this.state.list.filter((item)=> {

      return item.objectID !== id;
    });

    this.setState({list: updatedList})
  }



  
  
  
  
  
  
  

  render() {
    var titleName = "Learning React";
    return (
      <div className="App">
        {this.state.list.map(item =>
          <div key={item.objectID}>
            <span>
              <a href={item.url}>{item.title + " "}</a>
            </span>
            <span>{item.author + ", "}</span>
            <span>{"Austyn Washington : " + item.num_comments}</span>
            <span>{"  Number of points: " + item.points}</span>
            <span>
              <button
                onClick={() => this.onDismiss(item.objectID)}
                type="button"
                >
                Dismiss
                </button>
            </span>
          </div>
        )}


      
      {exampleObject.map(item =>
        <span>{"here " +item.firstname}</span>)}    
      </div>

    );
  }
}

 /*
  
  class ExplainBindingsComponent extends Component {
    
    constructor(){
      super();

      this.onClickMe = this.onClickMe.bind(this) //binding
      /*
      onClickMe = () => {
        console.log(this);
      }
      another form of binding as well
      
    }

    
    
    onClickMe() {
      console.log(this);

    }


    render() {
      return (
        <button 
          onClick={this.onClickMe.bind(this)}
          >
            Click Me
          </button>
      );
    }
  }
*/  
  


export default App; //The name of the class that extends to component that you'll be displaying
