import logo from "./logo.svg";
import React, { Component } from "react";
import "./App.css";
/*
const list = [
  {
    title: "React",
    url: "https://facebook.github.io/react/",
    author: "Jordan Walke",
    num_comments: 3,
    points: 4,
    objectID: 0,
  },
  {
    title: "Redux",
    url: "https://github.com/reactjs/redux",
    author: "Dan Abramov, Andrew Clark",
    num_comments: 2,
    points: 5,
    objectID: 1,
  },
];
*/

const list = [
  {
    title: "Current State here",
    url: "https://facebook.github.io/react/",
    num_comments: 5,
    points: 4,
    objectID: 0,
  },
  {
    title: "joe",
    url: "http",
    num_comments: 5,
    points: 4,
    objectID: 23,
  },
];

const exampleObject = [
  {
    firstname: "something",
    lastname: "here",
  },
];

//es6 destructuring
const programmer = {
  firstname: "Austyn",
  favLang: "Python",
};

const { firstname, favLang } = programmer;

console.log(firstname + " " + favLang);

//Destructuring arrays

const programmers = ["Austyn", "Washington", "Deqwavae"];
const [programmerOne, programmerTwo, programmerThree] = programmers;

console.log(programmerOne, programmerTwo, programmerThree);

const exampleService = {
  getNewService(user) {
    return user.firstname + " " + user.lastname;
  },
};

const key = "name";
const user = {
  [key]: "Robin",
};

function isSearched(searchTerm) {
  return function (item) {
    //condi
    return item.title.toLowerCase().includes(searchTerm.toLowerCase());
  };
}

//ES6
/*
const isSearched = searchTerm => item => 
  item.title.toLowerCase().includes(searchTerm.toLowerCase());

*/

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      list,
      searchTerm: "",
    };

    this.onDismiss = this.onDismiss.bind(this);
    this.onSearchChange = this.onSearchChange.bind(this);
  }
  //Leared on page 52
  onDismiss(id) {
    const updatedList = this.state.list.filter((item) => {
      return item.objectID !== id;
    });

    this.setState({ list: updatedList });
  }

//allows user to change the text to whatever they're typing, for example if you change the value of the search term as "orange juice" it would stay as orange juice 
  onSearchChange(event) {
    this.setState({ searchTerm: event.target.value  });
  }

  render() {
    const { searchTerm, list } = this.state;

    var someString = "something here"

    return (
      <div className="App">
        <Search value={searchTerm} onChange={this.onSearchChange} >
        {someString} 
        </Search>

        <Table list={list} pattern={searchTerm} onDismiss={this.onDismiss} />
      </div>
    );
  }
}

class Search extends Component {
  render() {
    const { 
      value, 
      onChange,
      children, 
    } = this.props;

    return (
      <form>
        {children}
        <input type="text" value={value} onChange={onChange} />
      </form>
    );
  }
}

class Table extends Component {
  render() {
    const { 
      list, 
      pattern,
      onDismiss
     } = this.props;
    
     return (
      <div>
        {list.filter(isSearched(pattern)).map((item) => (
          <div key={item.objectID}>
            <span>
              <a href={item.url}>{item.title}</a>
            </span>
            <span>{item.author}</span>
            <span>{item.num_comments}</span>
            <span>{item.points}</span>
            <span>
              <button onClick={() => onDismiss(item.objectID)} type="button">
                Dismiss
              </button>
            </span>
          </div>
        ))}
      </div>
    );
  }
}

/* Below className 

   <form>
          <input type="text"
          value={searchTerm}
          onChange={this.onSearchChange}
          />
        </form>

     
        {this.state.list.filter(isSearched(searchTerm)).map((item) => {
          const onHandleDismiss = () => this.onDismiss(item.objectID);

          return (
            <div key={item.objectID}>
              <span>
                <a href={item.url}>{item.title}</a>
              </span>
              <span>{item.author}</span>
              <span>{item.num_comments}</span>
              <span>{item.points}</span>
              <span>
                <button onClick={() => console.log(item.objectID)} type="button">
                  Dismiss
                </button>
              </span>
            </div>
          );
        })}

*/

//onClick={() => this.onDismiss(item.objectID)} //When using buttons in html they need to be within functions or else they're do whatever the action is immediately

/*
  
  class ExplainBindingsComponent extends Component {
    
    constructor(){
      super();

      this.onClickMe = this.onClickMe.bind(this) //binding
      /*
      onClickMe = () => {
        console.log(this);
      }
      another form of binding as well
      
    }

    
    
    onClickMe() {
      console.log(this);

    }


    render() {
      return (
        <button 
          onClick={this.onClickMe.bind(this)}
          >
            Click Me
          </button>
      );
    }
  }
*/

export default App; //The name of the class that extends to component that you'll be displaying
